import React from 'react';

export default class Input extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          value: '',
          showBorder: false,
          inputStyle: {border: '1px solid black'}
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      handleChange(event) {
        this.setState({value: event.target.value});
      }

      handleSubmit(event) {
        alert('An essay was submitted: ' + this.state.value);
        event.preventDefault();
      }

      render() {
        let inputStyle = {
            border: '1px solid black'
          };
          // change code below this line
            let moreThan15 = {
              border: '3px solid red'
            }
            let size = this.state.value.length;

            let valueStyle = inputStyle;

            size > 20 ? valueStyle=moreThan15 : valueStyle=inputStyle;



        return (
          <form onSubmit={this.handleSubmit}>
            <label>
              Enter Value:
              <input value={this.state.value} onChange={this.handleChange} style={valueStyle}/>
            </label>
            <br/>
            <label>{this.state.value}</label>
            <br/>
            <input type="submit" value="Submit" />
          </form>
        );
      }
  }
