import React from 'react';

export default class Input extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          value: '',
          showBorder: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      handleChange(event) {
        this.setState({value: event.target.value});
        let newValue = event.target.value
        if(newValue.length> 20)
        {
            this.setState({showBorder: true})
        }
      }

      handleSubmit(event) {
        alert('An essay was submitted: ' + this.state.value);
        event.preventDefault();
      }

      render() {

        let inputStyle = {
            border: '1px solid black'
          };
          // change code below this line\
          this.state.showBorder ? this.setState({inputStyle:'3px solid red'}) : inputStyle



        return (
          <form onSubmit={this.handleSubmit}>
            <label>
              Enter Value:
              <input value={this.state.value} onChange={this.handleChange} style={{border: this.state.showBorderborder && 'solid 1px red'}}/>
            </label>
            <label>{this.state.value}</label>
            <input type="submit" value="Submit" />
          </form>
        );
      }
  }
