import React from "react";

export default class Input extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      inputStyle: { border: "1px solid black" },
      counter: 0,
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    let over20 = false;
    let value = event.target.value;
    if (value.length > 20) {
      over20 = true;
    }
    this.setState({ value: value, showBorder: over20 });
  }

  handleSubmit(event) {
    let counter = this.state.counter;
    counter++;
    this.setState({ counter: counter });
    event.preventDefault();
  }

  render() {
    let inputStyle = {
      border: "1px solid black",
    };

    let moreThan20 = {
      border: "3px solid red",
    };
    let size = this.state.value.length;

    let valueStyle = inputStyle;

    this.state.showBorder
      ? (valueStyle = moreThan20)
      : (valueStyle = inputStyle);

    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Enter Value:
          <input
            value={this.state.value}
            onChange={this.handleChange}
            style={valueStyle}
          />
        </label>
        <br />

        {this.state.value && (
          <div>
            {" "}
            <label>{this.state.value}</label>
            <br />
          </div>
        )}
        <input type="submit" value="Submit" />
        <br />
        <label>Number of clicks: {this.state.counter}</label>

      </form>
    );
  }
}
